mapper.select:
SELECT cus_auth
FROM authority
WHERE cus_id = #{id}

SELECT cus_name
FROM customer
WHERE cus_phonenum= #{phonenum}

mapper.update:
UPDATE customer 
SET cus_phonenum= #{phonenum} , cus_nickname=#{nickname} 
WHERE cus_uid=#{uid}

UPDATE customer
SET cus_pw =#{pw}
WHERE cus_uid=#{uid}

	UPDATE customer
	SET cus_pw = #{pw}
	WHERE cus_id= #{id}
